import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    if (!query) {
      alert('Please enter a search term');
      return;
    }

    try {
      const response = await axios.post('http://localhost:3001/search', { query });
      setResults(response.data);
      setError('');
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to fetch results.');
    }
  };

  return (
    <div className="App">
      <header class="elementor-section elementor-top-section elementor-element elementor-element-d0343df elementor-section-content-middle elementor-section-boxed elementor-section-height-default elementor-section-height-default elementor-sticky elementor-sticky--effects elementor-sticky--active elementor-section--handles-inside" data-id="d0343df" data-element_type="section" data-settings="{&quot;background_background&quot;:&quot;classic&quot;,&quot;sticky&quot;:&quot;top&quot;,&quot;sticky_effects_offset&quot;:100,&quot;animation&quot;:&quot;none&quot;,&quot;sticky_on&quot;:[&quot;desktop&quot;,&quot;tablet&quot;,&quot;mobile&quot;],&quot;sticky_offset&quot;:0}" style="position: fixed; width: 510px; margin-top: 0px; margin-bottom: 0px; top: 0px;"></header>
      <header className="navbar">
        <h1>Facebook Ads Interest Search</h1>
      </header>

      <main>
        <section id="search-section">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Enter interest..."
            className="search-box"
          />
          <button onClick={handleSearch} className="search-button">Search</button>

          {error && <p className="error">{error}</p>}

          {results.length > 0 && (
            <table className="results-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Audience Size</th>
                  <th>Path 1</th>
                  <th>Path 2</th>
                  <th>Path 3</th>
                  <th>Description</th>
                  <th>Topic</th>
                </tr>
              </thead>
              <tbody>
                {results.map((interest) => (
                  <tr key={interest.id}>
                    <td>{interest.id}</td>
                    <td>{interest.name}</td>
                    <td>{interest.audience_size_lower_bound} - {interest.audience_size_upper_bound}</td>
                    <td>{interest.path[0]}</td>
                    <td>{interest.path[1] || 'N/A'}</td>
                    <td>{interest.path[2] || 'N/A'}</td>
                    <td>{interest.description || 'N/A'}</td>
                    <td>{interest.topic || 'N/A'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </section>
      </main>
    </div>
  );
}

export default App;
